body.script:
// Change this to the actual SVG URL you control (must allow CORS).
const svgUrl = 'https://your-trusted-host.example.com/assets/inner.svg';

async function loadSvg(url) {
  try {
    const res = await fetch(url, { mode: 'cors' });
    if (!res.ok) throw new Error('Network response not ok: ' + res.status);
    const text = await res.text();

    // Sanitize the SVG string. FORBID  and event attributes.
    const clean = DOMPurify.sanitize(text, {
      USE_PROFILES: { svg: true },
      FORBID_TAGS: ['script'],
      FORBID_ATTR: ['onload', 'onclick', 'onerror', 'onmouseover', 'onfocus']
    });

    // Insert sanitized SVG into the page
    const container = document.getElementById('svg-container');
    container.innerHTML = clean;
  } catch (err) {
    document.getElementById('svg-container').textContent = 'Failed to load SVG: ' + err.message;
    console.error(err);
  }
}

loadSvg(svgUrl);
